import {useState} from 'react'
import axios from 'axios'
import {Button, Form, FormGroup, FormText, Input} from "reactstrap"
import {Player} from '@lottiefiles/react-lottie-player'

const SignUpForm = () => {

  const [username, setUsername] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [delay, setDelay] = useState(null)
  const [usernameValid, setUsernameValid] = useState(true)
  const [emailValid = setEmailValid] = useState(false)
  const emailRegex = /\S+@\S+\.\S+/

  const signUp = () => {
    if (usernameValid && emailValid) {
      if (password === confirmPassword) {
        axios({
          method: 'POST',
          url: '',
          data: {
            username: username,
            email: email,
            password: password
          }
        })
        .then(resp => {
          console.log(resp)
        })
        .catch(error => {
          console.error(error.response)
        })
      }
    } else {
      return null
    }
  }

  return (
    <div>
      <h4>Sign Up</h4>
      <Player autoplay loop src = 'https://assets4.lottiefiles.com/packages/lf20_2qmtqq0y.json' style = {{ height: '300px', width: '300px' }}></Player>
      <hr/>
      <Form className="form">
        <FormGroup>
          <Input
            type="text"
            placeholder="Enter a username between 6 and 20 characters"
          />
        </FormGroup>
        <br/>
        <FormGroup>
          <Input
            type="email"
            placeholder="Email"
          />
        </FormGroup>
        <br/>
        <FormGroup>
          <Input
            type="password"
            placeholder="Minimum 6 characters"
          />
          <br/>
          <Input
            type="password"
            placeholder="Retype password to confirm"
          />
        </FormGroup>
        <hr/>
        <FormGroup>
          <Button color="success" disabled>Sign up with Google</Button>
        </FormGroup>
        <FormText>
          Already a member? <Button color="link">Login here.</Button>
        </FormText>
      </Form>
      <Button color="primary">Sign Up</Button>{' '}
      <Button color="secondary">Cancel</Button>
    </div>
  )
}

export default SignUpForm
