import {useState, useEffect} from 'react'

const Quiz = () => {

  const [questions, setQuestions] = useState([])
  const [loaded, setLoaded] = useState(false)
  const [qInd, setQInd] = useState(0)

  const insertCorr = (arr, corr) => {
    const randInd = Math.floor(Math.random() * 4)
    arr.splice(randInd, 0, corr)
  }

  useEffect(()=>{
    const fetchQuestion = async () => {
      let response = await fetch('https://opentdb.com/api.php?amount=5&category=9&difficulty=medium&type=multiple')
      let resp = await response.json()
      // console.log(resp.results)
      setQuestions(resp.results)
      console.log(questions[0])
      setLoaded(true)
      }
  })

  return (
    <div className='quiz'>
      <h1>Hello</h1>
    </div>
  )
}

export default Quiz
