const initState = {
  options: {
    loading: false,
    question_category: ``,
    question_difficulty: ``,
  }
}

const Reducer = (state = initState, action) => {
  switch (action.type) {
    case "CHANGE_LOADING":
      return {
        ...state,
        options: {
          ...state.options,
          loading: action.value
        }
      }

    case "CHANGE_CATEGORY":
      return {
        ...state,
        options: {
          ...state.options,
          question_category: action.value
        }
      }

    case "CHANGE_DIFFICULTY":
      return {
        ...state,
        options: {
          ...state.options,
          question_difficulty: action.value
        }
      }

    default:
      return state
  }
}

export default Reducer
