import React from 'react'
import './App.css'

function App() {

  const [questions, setQuestions] = useState([])
  const [loaded, setLoaded] = useState(false)
  const [qInd, setQInd] = useState(0)

  const loadQuestions = async () => {
    let response = fetch('https://opentdb.com/api.php?amount=10&category=9&difficulty=easy&type=multiple').then(response => response.json()).then(data => {
      setQuestions(data.results)
      setLoaded(true)
    })
  }

  return (
    <div className="App">
      {loaded && <div>
        <p className="Question">{questions[qInd].question}</p>
        </div>
      }
    </div>
  )
}

export default App
