  const apiCall = () => {
    axios.get('https://opentdb.com/api.php?amount=10&category=9&difficulty=easy&type=multiple')
    .then(response => {
      // console.log(response.data.results)
      setQuestions(response.data.results[0])
      console.log(questions)
    })
    .catch(error => {
      console.log(error)
    })
  }

  useEffect(()=>{
    apiCall()
  }, [])

  return (
    <>
