import '../Recipe.css'
import React, { useEffect, useState } from 'react';
import Recipe from "../containers/recipe/Recipe"
import Header from '../containers/recipe/Header';
import SearchIngredients from '../containers/recipe/SearchIngredients';
import SearchForm from '../containers/recipe/SearchForm';

function App() {

    const APP_ID = process.env.REACT_APP_APP_ID;
    const API_KEY = process.env.REACT_APP_API_KEY;

    const [recipes, setRecipes] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [search, setSearch] = useState("");
    const [firstSearch, setFirstSearch] = useState(true);
    const [noHits, setNoHits] = useState();
    const [query, setQuery] = useState("");

    useEffect(() => {
        getRecipes();
    }, [query]);

    const getRecipes = async () => {
        setIsLoading(true);
        const response = await fetch(
            `https://api.edamam.com/search?app_id=${APP_ID}&app_key=${API_KEY}&q=${query}`
        );

        const data = await response.json();
        if (data.hits.length !== 0) {
            setRecipes(data.hits);
            setNoHits(false);
            // console.log(data.hits);
            // console.log(data.hits.length);

        } else {
            // console.log(data.hits.length);
            setRecipes(data.hits);
            setNoHits(true);
        }
        setIsLoading(false);
    }


    return (
        <div className="App">

            <Header />

            <SearchForm
                setSearch={setSearch}
                setQuery={setQuery}
                setFirstSearch={setFirstSearch}
                search={search}
            />

            {firstSearch ? <SearchIngredients
                setQuery={setQuery}
                setSearch={setSearch}
                setFirstSearch={setFirstSearch}
            /> :
                <div></div>}

            {isLoading ? (<h5> Fetching Recipes from 3rd party... Wait a moment please </h5>) :

                <div className="recipes">

                    {recipes.map(recipe => (
                        <Recipe
                            key={Math.random(150)}
                            title={recipe.recipe.label}
                            calories={recipe.recipe.calories}
                            image={recipe.recipe.image}
                            source={recipe.recipe.url}
                            ingredients={recipe.recipe.ingredients}
                        />
                    ))}
                </div>
            }

            {!firstSearch && noHits ? <h5> No Recipes Found </h5> : <p> </p>}

        </div>
    );
}

export default App;
