import {useState, useEffect} from 'react'
import {useSelector, useDispatch} from 'react-redux'

const decodeHTML = (html) => {
  const txt = document.createElement('textarea')
  txt.innerHTML = html
  return txt.value
}

const Question = () => {

  const score = useSelector(state => state.score)
  // const questions = useSelector(state => state.questions)
  // const questionIndex = useSelector(state => state.index)
  const encodedQuestions = useSelector(state => state.questions)

  const [questions, setQuestions] = useState([])
  const [options, setOptions] = useState([])
  const [answerSelected, setAnswerSelected] = useState(false)
  const [selectedAnswer, setSelectedAnswer] = useState(null)
  const [answerCorrect, setAnswerCorrect] = useState(null)

  const dispatch = useDispatch()

  const question = questions[questionIndex]
  const answer = question && question.correct_answer

  const randomizeAnswer = (arr, corr) => {
    const randInd = Math.floor(Math.random() * 4)
    arr.splice(randInd, 0, corr)
  }

  const handleListItemClick = event => {
    setAnswerSelected(true)
    setSelectedAnswer(event.target.textContent)

    if (event.target.textContent === answer) {
      dispatch({
        type: 'SET_SCORE',
        score: score + 1
      })
    }

    if (questionIndex + 1 <= questions.length) {
      setTimeout(() => {
        setAnswerSelected(false)
        setSelectedAnswer(null)
        dispatch({
          type: 'SET_INDEX',
          index: questionIndex + 1
        })
      }, 2500)
    }
  }

  useEffect(() => {
    const decodedQuestions = encodedQuestions.map(q => {
      return {
        ...q,
        question: decodeHTML(q.question),
        correct_answer: decodeHTML(q.correct_answer),
        incorrect_answers: q.incorrect_answers.map(a => {
          decodeHTML(a)
        })
      }
    })
    setQuestions(decodedQuestions)
  }, [encodedQuestions])

  useEffect(() => {
    if (!question) {
      return null
    } else {
      let answers = randomizeAnswer(question.incorrect_answers, question.correct_answer)
      setOptions(answers)
    }
  }, [question])

  const getClass = option => {
    if (!answerSelected) {
      return null
    }
    if (option === answer) {
      return `correct`
    }
    if (option === selectedAnswer) {
      return `selected`
    }
  }

  if (!question) {
    return <div>Loading...</div>
  }

  return (
    <>
      <p>Question {questionIndex + 1}</p>
      <h3>{question.question}</h3>
      <ul>
        {options.map((option, i) => {
          <li key={i} onClick={handleListItemClick} className={getClass(option)}>
            {option}
          </li>
        })}
      </ul>
      <div>
        Score: {score} / {questions.length}
      </div>
    </>
  )
}

export default Question
